import React from "react";
import { Text, View } from "react-native";
import { WatchedT } from "../App";
import Btn from "../CustomComponents/CustomButton";
import Card from "../CustomComponents/CustomCard";
import Actors from "../modules/Actors";
import Genre from "../modules/genre";
import { isTvShow } from "../modules/movie-show";

interface WatchedCardProps {
    item: WatchedT;
    options: {
        actors: Actors[],
        genres: Genre[],
        authorBtn: (actor: Actors) =>void;
    }
}

const WatchedCard =  ({item, options}: WatchedCardProps) => {
    const actors = item.watch.actors.split(', ').map(actor =>{
        const actorR = options.actors.filter(act => act.id?.toString() === actor)[0];
        return (<View style = {{marginRight:2}} key={actorR.id}>
            <Btn  value={actorR.name} event={() => options.authorBtn(actorR)} bgColor='yellow' color="black" len='r1'/>
        </View>
        );
    });
    const genres =  item.watch.genre.split(', ').map(actor =>{
        const actorR = options.genres.filter(act => act.id?.toString() === actor)[0];
        return (<View style = {{marginRight:2}} key={actorR.id}>
            <Btn  value={actorR.name} event={()=>{return} } bgColor='lightgreen'  len='r1'/>
        </View>
        );
    });

    return (

        <Card len='r2' image={{uri: item.watch.image}} title={item.watch.name}>
        <Text>Actors: {actors}</Text>
        <Text>Genre: {genres}</Text>
        {isTvShow(item.watch)&&
            <Text>{item.watch.season} Episodes: {item.watch.episodes}</Text>}
        {isTvShow(item.watch)?
            <Text>Episode duration: {item.watch.duration}min</Text>:
            <Text>Movie duration: {item.watch.duration}</Text>
        }
        <Text>Rating: {item.watched.rating}</Text>
        <Text>Thoughts: {item.watched.thoughts}</Text>
        <Text>{item.watch.description}</Text>

    </Card>
     );
}

export default WatchedCard;
