import React from "react";
import { Text, View } from "react-native";
import Btn from "../CustomComponents/CustomButton";
import Card from "../CustomComponents/CustomCard";
import { RowLenght } from "../CustomComponents/shared/shared-interfaces";
import Actors from "../modules/Actors";
import Genre from "../modules/genre";
import {Movie} from "../modules/movie-show";

interface MovieCardProps {
    item: Movie;
    onDelete?:(item: Movie)=>void;
    onEdit?: (item: Movie)=>void;
    options: {
        actors: Actors[],
        genres: Genre[],
        authorBtn?: (actor: Actors) =>void;
        onWatched?:  (watch: Movie) =>void;
    }
    len: RowLenght
}

const MovieCard = ({item, onDelete, onEdit, options, len='r2'}: MovieCardProps) => {
    const actors = item.actors.split(', ').map(actor =>{
        const actorR = options.actors.filter(act => act.id?.toString() === actor)[0];
        return (<View style = {{marginRight:2}} key={actorR.id}>
            <Btn  value={actorR.name} event={options.authorBtn? () => options.authorBtn!(actorR) : ()=>{return}} bgColor='yellow' color="black" len='r1'/>
        </View>
        );
    });
    const genres = item.genre.split(', ').map(actor =>{
        const actorR = options.genres.filter(act => act.id?.toString() === actor)[0];
        return (<View style = {{marginRight:2}} key={actorR.id}>
            <Btn  value={actorR.name} event={()=>{return} } bgColor='lightgreen'  len='r1'/>
        </View>
        );
    });

    return (

        <Card len={len} image={{uri: item.image}} title={item.name}>
            <Text>Actors: {actors}</Text>
            <Text>Genre: {genres}</Text>
            <Text>Movie duration:{item.duration}</Text>
            <Text>{item.description}</Text>
            {options.onWatched &&
                <Btn value="Watched" event={()=>options.onWatched!(item)}/>}
            {onEdit &&
            <Btn value="Edit" bgColor="blue" event={()=>onEdit(item)}/>}
            {onDelete &&
            <Btn value="Delete" bgColor="red" event={()=>onDelete(item)}/>}
        </Card>
     );
}

export default MovieCard;
