import React from "react";
import { Dimensions, Image, StyleSheet, Text, View } from "react-native";
import Answer from "../modules/Answer";
import Question, { TypeAnswers } from "../modules/Question";
import Btn from "./.newFolder/CustomComponents/CustomButton";
import Card from "./.newFolder/CustomComponents/CustomCard";
import Draggable from "./Draggable";
import { Selected } from "./StartTest";

interface DropdownQuestionProps {
    item: Question;
    options: {
        selectedAnswers: Selected[];
        onSelectAnswer: (item: Answer, itemId: number)=>void;
    }
}


const DropdownQuestion =  ({item, options}: DropdownQuestionProps) => {
    const pictures = item.answers.map(ans =>  { return {id: ans.id, uri: ans.picture}});
    const texts = item.answers.map(ans =>  { return {id: ans.id, text: ans.text}});

    return (
        <View style={styles.container}>
            <Text style={styles.question}>{item.text}</Text>
            {item.answers.map(ans => {
                const index1 = Math.round(Math.random()*(pictures.length-1));
                const picture = pictures.splice(index1, 1)[0];
                const index2 = Math.round(Math.random()*(texts.length-1));
                const text = texts.splice(index2, 1)[0];

            return (<View key={ans.id} style={styles.row}>
                <Draggable dropAreas={[{position: {height: 150, px: Dimensions.get('window').width*0.785 - 150, py: 100, width: 150}}]} 
                    itemView={<Image source={{uri: picture.uri, width:100, height:100}} />}/>
                    <View style={styles.dropArea}>
                        <Text>{text.text}</Text>
                    </View>
            </View>
            );})}
        </View>
     );
}

const styles = StyleSheet.create({
    container: {
        display:'flex',
        flexDirection: 'column',
        gap: 10,
        alignContent: 'center',
        margin: 5,
        padding: 15,
        borderWidth: 2,  
        borderRadius: 25,
        width: "90%",
        borderColor:"#E0E0E0",
    },
    question: {
        fontSize: 40,
        alignSelf: "center",
        borderBottomWidth: 2,  
        borderBottomColor: "#E0E0E0",
    },
    row: {
        padding:5,
        display: 'flex',
        flexDirection:"row",
        justifyContent: 'space-between'
    },
    dropArea: {
        flexDirection: 'column',
        flexWrap: 'nowrap',
        borderWidth: 3,  
        borderColor: "#E0E0E1",
        height: 150,
        width: 150,
        padding: 10,
        alignItems: 'center',
        alignContent: 'center',
    },
});

export default DropdownQuestion;
