import React, { Component, useState } from "react";
import { Pressable, StyleSheet, Text, View } from "react-native";
import Answer from "../modules/Answer";
import Question from "../modules/Question";
import Btn from "./.newFolder/CustomComponents/CustomButton";
import Card from "./.newFolder/CustomComponents/CustomCard";

import Ionicons from '@expo/vector-icons/Ionicons';
import { Selected } from "./StartTest";

interface TestQuestionCardProps {
    item: Question;
    options: {
        selectedAnswers: Selected[];
        onSelectAnswer: (item: Answer)=>void;
    }
}

class TestQuestionCard extends Component<TestQuestionCardProps, {}> {

    render() {
        const {item, options} = this.props;
        const {selectedAnswers, onSelectAnswer} = options;
        const SelectedAnsw = selectedAnswers.filter(item => item.id === item.id)[0].answers;
        return (
            <Card len='r2' image={{uri: item.picture}} title={"Question"}>
                <Text>{item.text}</Text>
                <Text>Answers:</Text>
                {item.answers.map((answer, index) =>{
                    return <MyCheckbox  checked={SelectedAnsw.includes(answer)} onChange={onSelectAnswer} key={index} answer={answer} />
                })}

            </Card>
        );

    }
}
interface MyCheckboxProps{
    checked: boolean
    onChange: (ans: Answer, checked: boolean)=> void;
    answer: Answer;
}

function MyCheckbox({ checked, onChange, answer }: MyCheckboxProps) {

    function onCheckmarkPress() {
      onChange(answer, !checked);
    }

    return (
      <Pressable
        style={[styles.checkboxBase, checked && styles.checkboxChecked]}
        onPress={onCheckmarkPress}>
        {checked && <Ionicons name="checkmark" size={24} color="white" />}
        <Text>{answer.text}</Text>
      </Pressable>
    );
  }

  const styles = StyleSheet.create({
    checkboxBase: {
      width: 24,
      height: 24,
      justifyContent: 'center',
      alignItems: 'center',
      borderRadius: 4,
      borderWidth: 2,
      borderColor: 'coral',
      backgroundColor: 'transparent',
    },

    checkboxChecked: {
      backgroundColor: 'coral',
    },

    appContainer: {
      flex: 1,
      alignItems: 'center',
    },

    appTitle: {
      marginVertical: 16,
      fontWeight: 'bold',
      fontSize: 24,
    },

    checkboxContainer: {
      flexDirection: 'row',
      alignItems: 'center',
    },

    checkboxLabel: {
      marginLeft: 8,
      fontWeight: 500,
      fontSize: 18,
    },
  });


export default TestQuestionCard;
